import React, { Component } from 'react';

class Form extends Component {
  constructor() {
    super();

    this.handleClick = this.handleClick.bind(this);
    this.handleMouseEvent = this.handleMouseEvent.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

    this.state = {
      name: '',
      email: '',
      cpf: '',
      address: '',
      city: '',
      state: '',
      type: '',
      resume: '',
      job_title: '',
      description: '',
      alert: true,
    };
  };

  handleClick({ target }) {
    const { name } = target;
    const value = target.name === 'type' ? target.id :
      target.name === 'name' ? target.value.toUpperCase() : target.value;

    this.setState({ [name]: value });
  };

  handleMouseEvent() {
    if (this.state.alert) {
      alert('Preencha com cuidado esta informação.');
      this.setState({ alert: false });
    }
  };

  handleSubmit() {

  };

  render() {
    return (
      <form>
        <fieldset>
          <legend>Informações pessoais</legend>

          <div className="group">
            <label htmlFor="name">Nome:</label>
            <input type="text" id="name" name="name" maxLength="40" required
              onChange={this.handleClick} value={this.state.name}
            />

            <label htmlFor="email">Email:</label>
            <input type="text" id="email" name="email" maxLength="50" required
              onChange={this.handleClick} value={this.state.email}
            />
          </div>

          <label htmlFor="cpf">CPF:</label>
          <input type="text" id="cpf" name="cpf" maxLength="11" required
            onChange={this.handleClick} value={this.state.cpf}
          />

          <label htmlFor="address">Endereço:</label>
          <input type="text" id="address" name="address" maxLength="200" required
            onChange={this.handleClick} value={this.state.address}
          />
          {/* // TODO Remover qualquer caracter especial que seja digitado */}

          <label htmlFor="city">Cidade:</label>
          <input type="text" id="city" name="city" maxLength="28" required
            onChange={this.handleClick} value={this.state.city}
          />
          {/* // TODO Ao remover o foco desse campo (evento onBlur), verificar se o nome da cidade começa com números. Caso comece, limpar o campo. */}

          <label htmlFor="state">Estado:</label>
          <select name="state" id="state" required onChange={this.handleClick}>
            <option value="">Selecione um estado</option>
            <option value="PE">Pernambuco</option>
          </select>
          {/* // TODO criar campo com consulta api */}

          <label>Tipo:</label>

          <label htmlFor="house">
            <input type="radio" name="type" id="house" onChange={this.handleClick} required /> Casa </label>
          <label htmlFor="apartment">
            <input type="radio" name="type" id="apartment" onChange={this.handleClick} /> Apartamento </label>

        </fieldset>

        <fieldset>
          <legend>Informações sobre o último emprego</legend>

          <label htmlFor="resume">Resumo do currículo:</label>
          <textarea name="resume" id="resume" rows="15" maxLength="1000" required
            onChange={this.handleClick} value={this.state.resume}
          />

          <label htmlFor="job_title">Cargo:</label>
          <textarea name="job_title" id="job_title" rows="2" maxLength="40" required
            onChange={this.handleClick} value={this.state.job_title} onMouseEnter={this.handleMouseEvent}
          />

          <label htmlFor="description">Descrição do cargo:</label>
          <textarea name="description" id="description" rows="8" maxLength="500" required
            onChange={this.handleClick} value={this.state.description}
          />

        </fieldset>

        <button>Limpar</button>
        {/* // TODO limpar todos os campos do formulário e a <div> com seu currículo também. */}
        <button type="button" onClick={this.handleSubmit}>Cadastrar</button>
      </form>
    );
  };
};

export default Form;
